import React, { Component } from "react";

const api = {
  base: "https://api.rawg.io/api/",
  key: "de60294f61384714bcf0ed47cdcb2208"
};

class Games_app extends Component {
  constructor(props) {
    super(props);
    this.state = {
      query: "",
      date: "",
      games: {},
      isDelete: false
    };
  }

  async getGames() {
    var new_query = this.state.query.split(" ").join("-");
    const res = await fetch(`${api.base}games/${new_query}`);
    return res;
  }

  handleClick() {
    this.getGames()
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          games: result,
          query: ""
        });
      });
  }

  get_genre() {
    let games_array = this.state.games.genres.map((i, index) => {
      return <div key={index.toString()}>{i.name} </div>;
    });
    return games_array;
  }

  handleClickDelete() {
    this.setState({isDelete: true});
  }

  render() {
    return (
      <div>
        {this.state.isDelete === false ? (
        <Paper elevation={3} className={classes.root} >
          <Button style={{
            backgroundColor: "#DC143C",
            border: "0",
            borderradius: "3",
            boxshadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
            color: "white",
            height: "auto",
            width: "25px",
          }} variant="contained" onClick={() => this.handleClickDelete()}>X</Button>
          <form className={classes.root} noValidate autoComplete="off">
            <TextField value={this.state.query} onChange={(e) => this.setState({ query: e.target.value })} id="standard-basic" label="Enter the game name" />
          </form>
        <button onClick={() => this.handleClick()}>Search</button>
        {typeof this.state.games.genres !== "undefined" ? (
          <div>
            <h1>{this.state.games.name}</h1>
            <div>{this.get_genre()}</div>
            <img src={this.state.games.background_image}></img>
            <div>
              Mark: {this.state.games.rating} / 5 (
              {this.state.games.ratings_count} votes)
            </div>
            <h1>Summary:</h1>
            <div>{this.state.games.description_raw}</div>
          </div>
        ) : ("")}
        </Paper>
        ) : null}
      </div>
    );
  }
}

export default Games_app;
