import ReactDOM from "react-dom";
import React, { Component } from "react";

const api = {
  key: "3ca7ac710975ca6f256d01eabc07f6b0",
  base: "https://api.openweathermap.org/data/2.5/"
};

const dateBuilder = (d) => {
  let months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December"
  ];
  let days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday"
  ];

  let day = days[d.getDay()];
  let date = d.getDate();
  let month = months[d.getMonth()];
  let year = d.getFullYear();

  return `${day} ${date} ${month} ${year}`;
};

async function getWeatherAPI(query) {
  const res = await fetch (`${api.base}weather?q=${query}&appid=${api.key}`)
  return res
}



class Weather_app extends Component {
  constructor(props) {
    super(props);
    this.state = {
      query: "",
      weather: {}
    };
  }

  handleClick() {
    console.log(this.state.query);
    getWeatherAPI(this.state.query)
    .then(res => res.json())
    .then(result =>
      this.setState({query : "", weather : result} ))
  }

  KelvinToDegree(temp) {
    return (temp - 273.15);
  }

  render() {
    return (
      <div>
        <div>
          <input
            type="text"
            placeholder="Choose a city"
            onChange={(e) => this.setState({ query: e.target.value })}
            value={this.state.query}
          ></input>
          <button onClick={() => this.handleClick()}>Go</button>
        </div>
        {(typeof this.state.weather.main != "undefined") ? (
        <div>
          <div>
            <div>{this.state.weather.name}, {this.state.weather.sys.country}</div>
            <div>{dateBuilder(new Date())}</div>
          </div>
          <div>
            <div>{Math.round(this.KelvinToDegree(this.state.weather.main.temp))}Â°C</div>
        <div>{this.state.weather.weather[0].main}</div>
          </div>
        </div>
        ) : ('')}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(
    <Weather_app />
  ,
  rootElement
);

export default Weather_app;
