import React, { Component } from "react";
import classes from '../css/Widget.module.css';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import LocationCityIcon from '@material-ui/icons/LocationCity';
import ScheduleIcon from '@material-ui/icons/Schedule';
import CloudQueueIcon from '@material-ui/icons/CloudQueue';
import Divider from '@material-ui/core/Divider';

const api = {
  base: "https://api.lyrics.ovh/v1/"
};

class Lyrics_app extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist_query: "",
      title_query: "",
      lyrics: {},
      isDelete: false
    };
  }

  async getLyrics() {
    const res = await fetch(
      `${api.base}${this.state.artist_query}/${this.state.title_query}`
    );
    return res;
  }

  handleClick() {
    this.getLyrics()
      .then((res) => res.json())
      .then((result) => {
        this.setState({ lyrics: result, artist_query: "", title_query: "" });
      });
  }

  create_text() {
    let newText = this.state.lyrics.lyrics.split("\n").map((i) => {
      return <p>{i}</p>;
    });
    return newText;
  }

  handleClickDelete() {
    this.setState({isDelete: true});
  }

  render() {
    return (
      <div>
        {this.state.isDelete === false ? (
        <Paper elevation={3} className={classes.root} >
        <Button className={classes.buttonDelete} variant="contained" onClick={() => this.handleClickDelete()}>Delete</Button>
        <form className={classes.root} noValidate autoComplete="off">
            <TextField value={this.state.query} onChange={(e) => this.setState({ artist_query: e.target.value })} id="standard-basic" label="Enter the artist name" />
            <TextField value={this.state.query} onChange={(e) => this.setState({ title_query: e.target.value })} id="standard-basic" label="Enter the title" />
        </form>
        <Button className={classes.buttons} variant="contained" onClick={() => this.handleClick()}>Search</Button>
        {typeof this.state.lyrics.lyrics != "undefined" ? (
          <ListItem>
          <ListItemAvatar>
            <Avatar>
              <LocationCityIcon />
            </Avatar>
          </ListItemAvatar>
          <ListItemText primary={this.create_text()} />
        </ListItem>
          ) : (null)}
        </Paper>
        ) : null}
      </div>
    );
  }
}

export default Lyrics_app;
